import React, { useEffect, useState } from 'react';
import { Container, Form } from 'react-bootstrap';
import './typing.scss';
import '../../../../css/mediaqueries.scss';
import randomWords from 'random-words';

export default function Typing() {

    let [words, setWords] = useState(randomWords( { exactly: 100 }))
    let [accuracy, setAccuracy] = useState(new Array(100).fill(0));
    let [current, setCurrent] = useState(0);
    let [word, setWord] = useState("");

    function checkKey(event) {
        if(event.target.value[event.target.value.length - 1] === ' ') {
            setCurrent(current + 1);

            if(word === words[current]) {
                accuracy[current]++;
            } else {
                accuracy[current]--;
            }

            setWord("");
        } else {
            setWord(event.target.value);
        }
    }

    return (
        <Container className="media-width">

            <Container fluid className="d-flex words-container mb-3 p-5 flex-wrap">
                {words.map((v, i) => <Word key={i} word={v} active={i === current} result={accuracy[current]}/>)}
            </Container>

            <Form>
                <Form.Group controlId="formInput">
                    <Form.Control type="text" value={word} onChange={ev => checkKey(ev)}/>
                </Form.Group>
            </Form>
        </Container>
    );
}


function Word({word, active, result}) {

    return (
        <p className={`${active ? "word-active" : result === 1 ? "word-correct" : result === -1 ? "word-wrong" : 'text-muted'}`}>{word}</p>
    );
}
