import React, { useState } from 'react';
import { Container, Card } from 'react-bootstrap';
import './games.scss';


const NAMES = ["Typing Test", "Connect 4"];



export default function Games() {


    let [module, setModule] = useState(-1);

    return (
        <Container fluid className="d-flex flex-column align-items-center">
            <h3 className="p-5">Feel fee to try out these games made in React</h3>
            <div className="modules media-width d-flex justify-content-between">
                {NAMES.map((v, i) => <Module index={i} />)}
            </div>
        </Container>
    );
}


function Module({ index }) {

    return (
        <Card className="module">
            <Card.Img variant="top" src="holder.js/100px180" />
            <Card.Body>
                <Card.Title>Card Title</Card.Title>
                <Card.Text>
                    Some quick example text to build on the card title and make up the bulk of
                    the card's content.
            </Card.Text>
                <Button variant="primary">Go somewhere</Button>
            </Card.Body>
        </Card>
    );
}
